@section('js')
<script>
    function groupedBarChart() {
      return {
        chart: null,
        initializeChart() {
          const ctx = document.getElementById('groupedBarChart').getContext('2d');
          fetch('/api/report/get-data-use-report')
          .then(response => {
            if (!response.ok) {
              console.log(response);
              
              throw new Error('Network response was not ok ' + response.statusText);
            }
            return response.json();
          })
          .then(data => {
            this.chart = new Chart(ctx, {
              type: 'bar',
              data: {
                labels: data.data.arrLabels,
                datasets: [
                  {
                    label: 'Tổng',
                    data: data.data.arrTotal,
                    backgroundColor: '#682eec40',
                    borderColor: '#4c19c1',
                    borderWidth: 1
                  },
                  {
                    label: 'Đang sử dụng',
                    data: data.data.arrInuse,
                    backgroundColor: '#00ceb62b',
                    borderColor: '#068576',
                    borderWidth: 1
                  },
                ],
              },
              options: {
                responsive: true,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                },
                scales: {
                  x: {
                    stacked: false,
                  },
                  y: {
                    beginAtZero: true,
                  },
                },
              },
            });
          })
          .catch(error => {
            console.error('Error fetching chart data:', error);
          });
        },
      };
    }

    function doughnutChart() {
    return {
      chart: null,
      initializeChart() {
        const ctx = document.getElementById('doughnutChart').getContext('2d');

        fetch('/api/report/get-data-structure-report')
          .then(response => {
            if (!response.ok) {
              console.log(response);
              
              throw new Error('Network response was not ok ' + response.statusText);
            }
            return response.json();
          })
          .then(data => {
            
            this.chart = new Chart(ctx, {
              type: 'doughnut',
              data: {
                labels: ['Đang sử dụng', 'Chưa sử dụng', 'Hỏng', 'Mất, hủy, thanh lý', 'Khác'],
                datasets: [{
                  label: 'Số tài sản',
                  data: data.data.values,
                  backgroundColor: [
                    '#00ceb67a',
                    '#03a9fdc9',
                    '#ff9500b3',
                    '#ff4b4bc7',
                    '#696969',
                  ],
                  hoverOffset: 4,
                }]
              },
              options: {
                responsive: true,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                  tooltip: {
                    enabled: true,
                  }
                },
                cutout: '60%',
              }
            });
          })
          .catch(error => {
            console.error('Error fetching chart data:', error);
          });
      },
    };
  }
</script>
<script>
    document.addEventListener('alpine:init', () => {
    Alpine.data('useReport', () => ({

    }));
});
</script>
@endsection